import { Infer, InferTuple, Obj, Struct, UnionToIntersection } from "../types";
/**
 * Creates a new struct that merges other structs.
 * Behaves like typescript `interface A extends B, C, D {}`
 */
export declare const intersection: <A extends Struct<Obj, any>, B extends Struct<Obj, any>[]>(structs: [A, ...B]) => Struct<Infer<A> & UnionToIntersection<InferTuple<B, B["length"]>[number]>, any>;
