import { Infer, InferTuple, Struct } from "../types";
/**
 * Creates a new struct that accepts any of the given structs.
 * It behaves like a union type: A | B | C.
 */
export declare const union: <A extends Struct<any, any>, B extends Struct<any, any>[]>(structs: [A, ...B], msg?: string) => Struct<Infer<A> | InferTuple<B, B["length"]>[number], any>;
